import React, { useState, useEffect } from 'react';
import background from "../background-globe-animated.svg";
import * as ReactBootStrap from 'react-bootstrap';
import { RemoveScrollBar } from 'react-remove-scroll-bar';
import './IpLookup.css'
import ReturnDetails from './ReturnIpDetails';

function IpLookup() {
  const [ipAddress, setIp] = useState('')
  const [ipDetails, setIpDetails] = useState([])
  const [ipLoading, setIpLoading] = useState(true)

  const handleIPInput = (e) => {
    e.preventDefault()
    // if (/\D/g.test(e.target.value)) {
    //   e.target.value = e.target.value.replace(/\D/g, '');
    // }
    setIp(e.target.value)
  }

  const toggleIpButtonState = async (e) => {
    if (ipDetails) { setIpDetails([]) }
    e.preventDefault()
    setIpLoading(false);
    await fetch('https://ipapi.co/' + ipAddress + '/json/')
      .then(async response => response.json())
      .then((data) => {
        setIpDetails(ipDetails => [...ipDetails, data])
      }
      )
    setIpLoading(true);
  };
  var rootStyle = {
    backgroundImage: `url(${background})`,
    color: 'white',
    height: '100vh',
    width: '100vw',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    overflow: 'hidden',
    backgroundPosition: 'center',
    fontFamily: 'Overpass,sans-serif',
    textAlign: 'left'
  }

  return (
    <div className='App' style={rootStyle}>
      <RemoveScrollBar />
      <div>
        <h2>IP Search</h2>
        <div>
          <h5>Enter IP Address: </h5>
          <input className="InputBox" value={ipAddress} onChange={(e) => handleIPInput(e)} placeholder="8.8.8.8" />
          <button className="ClickMeButton" onClick={(e) => toggleIpButtonState(e)}> View Info! </button>
        </div>
        <div>
          <br></br>
        </div>
        <div>
          {ipLoading ? <ReturnDetails csv={highRiskData} intlPhoneDetails={intlPhoneDetails} liteCsv={liteData} /> :
            <ReactBootStrap.Spinner animation="border" variant="info" />}
        </div>
      </div>
    </div>
  );
}

export default IpLookup;
