import React, {Component} from 'react';

let phoneNumber;

export class App extends Component{
  constructor() {
    super();
    this.state = {
      phone: '',
      countryPrefix: ''
    }
    this.handlechange = this.handlechange.bind(this);
  }

  handlechange(e) {
    phoneNumber = this.state.phone;
    const accessKey = '799b6b058858fbf11920dbc603e34094';
      fetch(`http://apilayer.net/api/validate?access_key=${accessKey}
      &number=${phoneNumber}&country_code=&format=1`)

      .then(response => response.json())
      .then(data => this.setState({
        countryPrefix: data['country_prefix']
        }) )
      .catch(error => console.log('failed to fetch', error));
    this.setState({phone: e.target.value});
  }

  render() {
    return(
    <div>
      <p>Phone Number: </p><input type='text' onChange={this.handlechange}/>
      <h1> Your phone number is {this.state.phoneNumber}</h1>
    </div>);
  }
}
  export default App;
