import React, { useState, useEffect } from 'react';
import ReturnDetails from './components/ReturnDetails';
//import useNumberDetails from './components/useNumberDetails';
import * as d3 from 'd3';
import data from "./BandwidthHighRiskBlock.csv";
import lite from "./lite.csv";
import background from "./background-globe-animated.svg"
import "./App.css";
import SplitPane from 'react-split-pane'

function App() {

  const [phone, setPhone] = useState('')
  const [phoneDetails, setPhoneDetails] = useState([])
  const [highRiskData, setHighRiskData] = useState([])
  const [liteData, setLiteData] = useState([])
  const [intlPhone, setIntlPhone] = useState('')
  const [intlPhoneDetails, setIntlPhoneDetails] = useState([])

  useEffect(() => {
    d3.csv(data).then(function (dta) {

      setHighRiskData(dta)

    })
  }, [])

  useEffect(() => {
    d3.csv(lite).then(function (liteDta) {

      setLiteData(liteDta)

    })
  }, [])

  const handleInput = (e) => {
    e.preventDefault()
    setPhone(e.target.value)
    //console.log(phone)
  }
  const handleIntlInput = (e) => {
    e.preventDefault()
    setIntlPhone(e.target.value)
    //console.log(phone)
  }
  const toggleButtonState = async (e) => {
    if (phoneDetails) { setPhoneDetails([]) }
    e.preventDefault()
    fetch('http://apilayer.net/api/validate?access_key=1fcc33e69f7fee637d15338c111fe68f&number=' + phone + '&country_code=&format=1')
      .then(response => response.json())
      .then((data) => {

        setPhoneDetails(phoneDetails => [...phoneDetails, data])
        console.log('in the toggle fetch')
      }
      )
  };
  const toggleIntlButtonState = async (e) => {
    if (intlPhoneDetails) { setIntlPhoneDetails([]) }
    e.preventDefault()
    const requestOptions = {
      method: 'GET',
      headers: { 'Authorization': 'Basic dG5sb29rdXBhcGk6QkBuZHcxZHRoMjAyMSE=' },
      body: JSON.stringify({ tns: ["17754127163"] })
    };
    fetch('https://0xi9lxezba.execute-api.us-east-2.amazonaws.com/test/tnlookup', requestOptions)
      console.log('in the toggleIntlButtonState: ')
      .then(async response => {
        console.log (response)
        const isJson = response.headers.get('content-type')?.includes('application/json');
        const data = isJson && await response.json();

        // check for error response
        if (!response.ok) {
          // get error message from body or default to response status
          const error = (data && data.message) || response.status;
          return Promise.reject(error);
        }

        //this.setState({ postId: data.id })
      })
      .catch(error => {
        this.setState({ errorMessage: error.toString() });
        console.error('There was an error!', error);
      });
  };
  var rootStyle = {
    backgroundImage: `url(${background})`,
    color: 'white',
    height: '100vh',
    width: '100vw',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    overflow: 'hidden',
    backgroundPosition: 'center',
    fontFamily: 'Overpass,sans-serif',
    textAlign: 'left',
    //alignItems: 'left',
  }

  return (
    // <div className={splitScreen}>
    //   <div style={rootStyle}>
    //     {/* <center> */}
    //       <div>
    //         <h1>Number Info</h1>
    //         <div className={topPane}>
    //             <h5>Enter Phone Number: </h5>
    //             <input className = "InputBox" value = {phone} onChange = {(e) => handleInput(e)} /> 
    //             <button className = "ClickMeButton" onClick={(e) => toggleButtonState(e)}> View Info! </button>
    //         </div>
    //       </div>
    //       <div>
    //         <br></br>
    //       </div>
    //       <div>
    //         <ReturnDetails  csv = {highRiskData} phoneDetails = {phoneDetails} liteCsv = {liteData}/> 
    //       </div>
    //       <div className={bottomPane}>
    //             <h5>Enter Local Phone Number: </h5>
    //             <input className = "InputBox" value = {phone} onChange = {(e) => handleInput(e)} /> 
    //             <button className = "ClickMeButton" onClick={(e) => toggleButtonState(e)}> View Info! </button>
    //         </div>
    //     {/* </center> */}
    //   </div>
    // </div>,

    <div className='App' style={rootStyle}>
      <center>
        <h1>Number Info Tool</h1>
        <SplitPane split='vertical' minSize='50%' defaultSize='50%'>
          <div>
            <h3>Local Number Search</h3>
            <div>
              <h5>Enter Local Phone Number: </h5>
              <input className="InputBox" value={phone} onChange={(e) => handleInput(e)} />
              <button className="ClickMeButton" onClick={(e) => toggleButtonState(e)}> View Info! </button>
            </div>
            <div>
              <br></br>
            </div>
            <div>
              <ReturnDetails csv={highRiskData} phoneDetails={phoneDetails} liteCsv={liteData} />
            </div>
          </div>
          <div>
            <h3>International Number Search</h3>
            <div>
              <h5>Enter International Phone Number: </h5>
              <input className="InputBox" value={intlPhone} onChange={(e) => handleIntlInput(e)} />
              <button className="ClickMeButton" onClick={(e) => toggleIntlButtonState(e)}> View Info! </button>
            </div>
            <div>
              <br></br>
            </div>
            <div>
              {/* <ReturnDetails  csv = {highRiskData} phoneDetails = {phoneDetails} liteCsv = {liteData}/>  */}
              Nothing to see here
            </div>
          </div>
        </SplitPane></center>
    </div>

  );

}

export default App;
