import React, { useState, useEffect } from 'react';
import background from "../background-globe-animated.svg";
import * as ReactBootStrap from 'react-bootstrap';
import { RemoveScrollBar } from 'react-remove-scroll-bar';

function IpLookup() {
  const [ipAddress, setIp] = useState('')
  const [ipDetails, setIpDetails] = useState([])
  const [ipLoading, setIpLoading] = useState(true)

  const handleIPInput = (e) => {
    e.preventDefault()
    // if (/\D/g.test(e.target.value)) {
    //   e.target.value = e.target.value.replace(/\D/g, '');
    // }
    setIp(e.target.value)
  }

  const toggleIpButtonState = async (e) => {
    if (ipDetails) { setIpDetails([]) }
    e.preventDefault()
    setIpLoading(false);
    await fetch('https://ipapi.co/' + ipAddress + '/json/')
      .then(async response => response.json())
      .then((data) => {
        setIpDetails(ipDetails => [...ipDetails, data])
      }
      )
    setIpLoading(true);
  };
  var rootStyle = {
    backgroundImage: `url(${background})`,
    color: 'white',
    height: '100vh',
    width: '100vw',
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    overflow: 'hidden',
    backgroundPosition: 'center',
    fontFamily: 'Overpass,sans-serif',
    textAlign: 'left'
  }

  return (
    <div className="about">
      <div class="container">
        <div class="row align-items-center my-5">
          <div class="col-lg-7">
            <img
              class="img-fluid rounded mb-4 mb-lg-0"
              src="http://placehold.it/900x400"
              alt=""
            />
          </div>
          <div class="col-lg-5">
            <h1 class="font-weight-light">About</h1>
            <p>
              Lorem Ipsum is simply dummy text of the printing and typesetting
              industry. Lorem Ipsum has been the industry's standard dummy text
              ever since the 1500s, when an unknown printer took a galley of
              type and scrambled it to make a type specimen book.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default IpLookup;
